import React from 'react';
import { useState, useEffect } from 'react';
import { Card, CardText, CardTitle, Button, Form, FormGroup, Label, Input, Jumbotron, Container, Col, Row } from 'reactstrap';
import axios from 'axios';

import '../../../App.css';


const Article = (props) => {
  let [search, setSearch] = useState("");
  let [nome, setNome] = useState("");
  let [descricao, setDescricao] = useState("");
  let [codigo, setCodigo] = useState("");
  let [valor, setValor] = useState("");
  let [quantidade, setQuantidade] = useState("");

  async function buscar(){
    axios.post(`http://localhost:3002/viewproduto`, {
      nome: search
    }).then((results) => {
      if(search === ""){
        alert("Preencha o campo!");
      }
      else if(results.data.found === false){
        alert("produto não encontrado!");
      }
      
    });
  }

  return (
    <div className="corpo ">
      <Jumbotron fluid className="jum bg-dark">
        <Container>
          <Row>
            <Col sm="6">
              <Card body className="options bg-info col-" >
                <CardTitle>Buscar produtos pelo Nome:</CardTitle>
                <CardText>
                  <Form>
                    <FormGroup>
                      <Input className="input" type="text" name="name" value={search} onChange={(e) => (setSearch(e.target.value))} placeholder="Insira o nome" />
                    </FormGroup>
                  </Form>
                </CardText>
                <Button className="button" onClick={buscar}>Pesquisar</Button>
              </Card>
            </Col>
            <Col sm="6">
              <Card body className="options bg-info col-" >
                <CardTitle>Resultado:</CardTitle>
                <CardText>
                  <Form>
                    <FormGroup>
                      <Label for="form">Nome</Label>
                      <Input className="input" disabled name="name" id="name" />
                    </FormGroup>
                    <FormGroup>
                      <Label for="form">Descrição:</Label>
                      <Input className="input" disabled name="description" id="description" />
                    </FormGroup>
                    <FormGroup>
                      <Label for="form">Código:</Label>
                      <Input className="input" disabled name="code" id="code" />
                    </FormGroup>
                    <FormGroup>
                      <Label for="form">Quantidade:</Label>
                      <Input className="input" disabled name="quant" id="quant" />
                    </FormGroup>
                    <FormGroup>
                      <Label for="form">Valor:</Label>
                      <Input className="input" disabled name="price" id="price" />
                    </FormGroup>
                  </Form>
                </CardText>
              </Card>
            </Col>
          </Row>
        </Container>
      </Jumbotron>
    </div>
  );
};

export default Article;
